// Search.jsx

import React, { useState } from "react";
import axios from "axios";
import "bulma/css/bulma.min.css";

const Search = ({ onSearch }) => {
  const [query, setQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleInputChange = (e) => {
    setQuery(e.target.value);
  };

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        `http://localhost:3001/api/productos/search?q=${query}`
      );

      setSearchResults(response.data);
      onSearch(response.data);
    } catch (error) {
      console.error("Error fetching search results", error);
    }
  };

  return (
    <div className="field has-addons">
      <div className="control">
        <input
          className="input"
          type="text"
          placeholder="Buscar..."
          value={query}
          onChange={handleInputChange}
        />
      </div>
      <div className="control">
        <button className="button is-info" onClick={handleSearch}>
          Buscar
        </button>
      </div>

      {searchResults.length > 0 && (
        <div className="mt-3">
          <p className="title is-5">Resultados de la búsqueda:</p>
          <ul>
            {searchResults.map((producto) => (
              <li key={producto.id}>{producto.nombre}</li>
              // Puedes mostrar otros detalles del producto según tus necesidades
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default Search;
