import React, { useState, useEffect } from "react";
import axios from "axios";
import "bulma/css/bulma.min.css";
import CardApp from "./CardApp";

const SearchGames = ({ onSearchResults }) => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [error, setError] = useState(null);

  const handleInputChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        `http://localhost:3001/api/productos/search?searchTerm=${searchTerm}`
      );

      if (response.status !== 200 || response.data.length === 0) {
        setError("No hay resultados para esta búsqueda.");
        setSearchResults([]);
        onSearchResults([]);
        return;
      }

      setError(null);
      setSearchResults(response.data);
      onSearchResults(response.data);
    } catch (error) {
      setError("Error al buscar juegos.");
      console.error("Error searching for games:", error);
    }
  };

  // useEffect para realizar la búsqueda cuando searchTerm cambie
  useEffect(() => {
    // Realizar la búsqueda solo si el término de búsqueda no está vacío
    if (searchTerm.trim() !== "") {
      handleSearch();
    }
  }, [searchTerm]);

  return (
    <div className="container">
      <h2 className="title is-3">Buscar Juegos</h2>
      <div className="field has-addons">
        <div className="control is-expanded">
          <input
            className="input"
            type="text"
            value={searchTerm}
            onChange={handleInputChange} // Agregar el manejador de cambios aquí
            placeholder="Ingrese el nombre del juego"
          />
        </div>
        <div className="control">
          <button className="button is-primary" onClick={handleSearch}>
            Buscar
          </button>
        </div>
      </div>
      {error ? <p className="has-text-danger">{error}</p> : null}

      {/* Mostrar las cards de los juegos encontrados */}
      {searchResults.length > 0 && (
        <div className="columns is-multiline pt-4">
          {searchResults.map((game) => (
            <CardApp key={game.id} {...game} />
          ))}
        </div>
      )}
    </div>
  );
};

export default SearchGames;
