import React, { useState } from "react";
import axios from "axios";
import Card from "./Card";
import "bulma/css/bulma.min.css";

const SearchGames = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.get(
        `http://localhost:3001/api/productos/search?q=${searchTerm}`
      );
      setSearchResults(response.data);
    } catch (error) {
      console.error("Error searching for games:", error);
    }
  };

  return (
    <div className="container">
      <h2 className="title is-3">Buscar Juegos</h2>
      <div className="field has-addons">
        <div className="control is-expanded">
          <input
            className="input"
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Ingrese el nombre del juego"
          />
        </div>
        <div className="control">
          <button className="button is-primary" onClick={handleSearch}>
            Buscar
          </button>
        </div>
      </div>
      {searchResults.length > 0 ? (
        <div>
          <h3 className="subtitle is-4">Resultados de la búsqueda:</h3>
          <div className="columns is-multiline">
            {searchResults.map((item) => (
              <Card key={item.id} {...item} />
            ))}
          </div>
        </div>
      ) : (
        <p>No hay resultados de búsqueda</p>
      )}
    </div>
  );
};

export default SearchGames;
