// Importa los módulos necesarios
import React, { useState, useEffect } from "react";
import SearchGames from "../SearchGames";
import CardApp from "../CardApp";
import axios from "axios";

// Componente Home
export function Home() {
  // Estado local: 'productos' guarda los productos, inicializado como un array vacío
  const [productos, setProductos] = useState([]);

  // Estado local: 'loading' indica si los datos están siendo cargados
  const [loading, setLoading] = useState(true);

  // Estado local: 'error' guarda mensajes de error, inicializado como null
  const [error, setError] = useState(null);

  // Función para manejar los resultados de la búsqueda y actualizar 'productos'
  const handleSearchResults = (results) => {
    setProductos(results);
  };

  // Efecto para cargar los productos cuando el componente se monta
  useEffect(() => {
    const fetchData = async () => {
      try {
        // Realizar la solicitud al servidor Express para obtener los productos
        const response = await axios.get("http://localhost:3001/api/productos");

        // Actualizar el estado de 'productos' y 'loading'
        setProductos(response.data);
        setLoading(false);
      } catch (error) {
        // Manejar errores y actualizar el estado de 'loading' y 'error'
        setLoading(false);
        setError(
          "Error al cargar los productos. Inténtalo de nuevo más tarde."
        );
      }
    };

    // Llamar a la función para cargar los productos
    fetchData();
  }, []); // La dependencia está vacía para que el efecto se ejecute solo una vez al montar el componente

  // Mostrar mensaje de error si hay un error
  if (error) {
    return <p className="has-text-danger">{error}</p>;
  }

  // Mostrar mensaje de carga mientras se obtienen los datos
  if (loading) {
    return <p>Cargando productos...</p>;
  }

  // Renderizar la interfaz con los productos
  return (
    <>
      <SearchGames onSearchResults={handleSearchResults} />
      {productos.length > -1 ? (
        <div>
          <h2>Productos Disponibles:</h2>
          <div className="columns is-multiline pt-4">
            {productos.map((productos) => (
              <CardApp key={productos.id} {...productos} />
            ))}
          </div>
        </div>
      ) : (
        <p>No hay productos disponibles.</p>
      )}
    </>
  );
}
