// En tu componente App.jsx

import React, { useState } from "react";
import Navbar from "./components/Navbar";
import Card from "./components/Card";
import Search from "./components/Search"; // Asegúrate de utilizar el nombre correcto

const App = () => {
  const [searchResults, setSearchResults] = useState([]);

  const handleSearchResults = (results) => {
    setSearchResults(results);
  };

  return (
    <div>
      <header>
        <Navbar />
        <Search onSearch={handleSearchResults} />
      </header>
      <main>
        {searchResults.length > 0 && (
          <div>
            <p className="title is-5">Resultados de la búsqueda:</p>
            <ul>
              {searchResults.map((producto) => (
                <li key={producto.id}>{producto.nombre}</li>
                // Puedes mostrar otros detalles del producto según tus necesidades
              ))}
            </ul>
          </div>
        )}
        <Card />
      </main>
    </div>
  );
};

export default App;
